            self.driver.refresh()
            self.driver.get(self.url_dict.get('login'))
            time.sleep(2)
            cookie = {}
            for c in self.driver.get_cookies():
                cookie.update({c['name']: c['value']})
                if c['name'] == 'XSRF-TOKEN':
                    self.xsrf_token = c['value']
